using System.Collections; using System.Collections.Generic; using UnityEngine; using Character;  public class ES2UserType_CharacterMerchant : ES2Type{     public override void Write(object obj, ES2Writer writer){         Merchant data = (Merchant)obj;         writer.Write(data.Id);         writer.Write(data.NumberOfGoods);         writer.Write(data.GoodsLevel);         writer.Write(data.Abilities);         writer.Write(data.ContainerPostion);         writer.Write(data.ContainerRotate);      }        public override object Read(ES2Reader reader){         var id = reader.Read<System.Int32>();         var numberOfGoods = reader.Read<System.Int32>();         var goodsLevel = reader.Read<System.Int32>();
		var abilities = reader.ReadDictionary<Parameter.CharacterParameters.FriendlyAbility, System.Int32>();
		var containerPos = reader.Read<Vector3>();
		var continerRotate = reader.Read<Quaternion>();         Merchant data = new Merchant(id,goodsLevel,numberOfGoods,abilities,containerPos,continerRotate);         return data;     }      public override void Read(ES2Reader reader, object c){         var id = reader.Read<System.Int32>();         var numberOfGoods = reader.Read<System.Int32>();         var goodsLevel = reader.Read<System.Int32>();         var abilities = reader.ReadDictionary<Parameter.CharacterParameters.FriendlyAbility, System.Int32>();
		var containerPos = reader.Read<Vector3>();
		var continerRotate = reader.Read<Quaternion>();
		Merchant data = new Merchant(id, goodsLevel, numberOfGoods, abilities, containerPos, continerRotate);          Merchant merchant = (Merchant)c;         merchant = data;     }        /* ! Don't modify anything below this line ! */     public ES2UserType_CharacterMerchant():base(typeof(Merchant)){} } 