using System.Collections;using System.Collections.Generic;using UnityEngine;using Character;using Item;using MasterData;public class TalkTest : MonoBehaviour {    public Container con;    public Container plCon;    public Container maCon;    public Container matsuco;    public HealItem hphItem;    public HealItem mphItem;    public Hero hero;	// Use this for initialization	void Start () {        hero = new Hero(JobMasterManager.getJobFromId(0), plCon);        plCon.setCharacter(hero);        hero.dammage(20,Skill.ActiveSkillParameters.AttackSkillAttribute.PHYSICAL);        hero.minusMp(20);        Debug.Log("beforeheal hp " + hero.getHp() + "/" + hero.getMaxHp() + " mp " + hero.getMp() + "/" + hero.getMaxMp());        Citizen civ = CitizenMasterManager.getCitizenFromId(0);        con.setCharacter(civ);        Merchant mer = MerchantMasterManager.getMerchantFromId(0);        maCon.setCharacter(mer);        Merchant mer2 = MerchantMasterManager.getMerchantFromId(2);        matsuco.setCharacter(mer2);        hphItem = HealItemMasterManager.getHealItemFromId(0);        mphItem = HealItemMasterManager.getHealItemFromId(1);        hero.addItem(hphItem);	}		// Update is called once per frame	void Update () {        if (Input.GetKeyDown(KeyCode.H)) {
            hphItem.use(hero);
            mphItem.use(hero);
            Debug.Log("afterheal hp " + hero.getHp() + "/" + hero.getMaxHp() + " mp " + hero.getMp() + "/" + hero.getMaxMp());        }	}}